public class Schedule {    private Course[] courses;    private int numOfCourses;    private final int MAX_COURSES = 20;    public Schedule() {        this.courses = new Course[MAX_COURSES];        numOfCourses = 0;    }    public boolean addCourse(Course c) {        if (numOfCourses == MAX_COURSES)            return false;        courses[numOfCourses++] = c;        return true;    }    public String toString() {        String str = "";        for (int i = 0; i < numOfCourses; i++) {            str = "Name Course: " + this.courses[i].getName() + "\n" +                    "Day Course: " + this.courses[i].getDay() + "\n" +                    "Hour of Course: " + this.courses[i].getBeginHour();        }        return str;    }    public Course getFirstlnDay(int day) {        Course time = null;        for (int i = 0; i < numOfCourses; i++) {            if (day == courses[i].getDay()) {                if (time == null || courses[i].getBeginHour() < time.getBeginHour())                    time = courses[i];            }        }        if (time==null){            return null;        }        return new Course(time);    }    public Course getNaxtlnDay(Course c){        int day = c.getDay();        Course good = null;        for (int i = 0; i < numOfCourses; i++) {            if(day==courses[i].getDay() && courses[i].getBeginHour() > c.getBeginHour()){                if(good==null||courses[i].getBeginHour()<good.getBeginHour()){                    good = courses[i];                }            }        }        if (good==null){            return null;        }        return new Course(good);    }}class testerSchedule {    public static void main(String[] args) {        Schedule schedule = new Schedule();        Course course = new Course("A", 1, 9);        Course course2 = new Course("B", 1, 8);        Course course1 = new Course("C", 2, 20);        schedule.addCourse(course);        schedule.addCourse(course2);        schedule.addCourse(course1);        System.out.println(schedule.getFirstlnDay(1));    }}